function sortObjectsByType(data) {
    let result = {
        "FAQ": [],
        "Files": [],
        "Link": [],
        "Manual": []
    };

    data.forEach(item => {
        if (result[item.type]) {
            result[item.type].push(item);
        } else {
            result[item.type] = [item];
        }
    });

    return result;
}

